package LC2024

class `1508` {
    fun rangeSum(nums: IntArray, n: Int, left: Int, right: Int): Int {
        val MODULO = 1_000_000_000 + 7
        val prefix = IntArray (n + 1) { 0 }

        for (i in 0 until nums.size) {
            prefix[i + 1] = (prefix[i] + nums[i]) % MODULO
        }

        val sums = mutableListOf<Int>()

        for (distance in 0 until nums.size) {
            for (start in 0 until nums.size) {
                if (distance + start >= n) break
                val endIndex = start + distance
                val sum = (prefix[endIndex + 1] - prefix[start]) % MODULO
                sums.add(sum)
                //println("need the sum between $start (${nums[start]}) and $endIndex (${nums[endIndex]}) which is $sum")


            }

        }

        sums.sort()

        var ans = 0
        for (i in left - 1 until right) {
            ans = (ans + sums[i]) % MODULO
        }
        return ans


    }
}

